/* JS Utility Template */
export function sum(a, b) {
  if (typeof a !== 'number' || typeof b !== 'number') throw new TypeError('numbers only');
  return a + b;
}

export default function main() {
  const out = sum(2, 3);
  console.log('sum =', out);
}

